import{_ as e,c as t,o as a,a as r}from"./app.70277779.js";const m=JSON.parse('{"title":"Introduction","description":"","frontmatter":{"title":"Introduction"},"headers":[{"level":2,"title":"Want to see more?","slug":"want-to-see-more","link":"#want-to-see-more","children":[]}],"relativePath":"guides/getting-started/introduction.md"}'),o={name:"guides/getting-started/introduction.md"},i=r('<h1 id="welcome-to-alis-exchange" tabindex="-1">Welcome to alis.exchange <a class="header-anchor" href="#welcome-to-alis-exchange" aria-hidden="true">#</a></h1><blockquote><p><strong><em>&quot;Our job as builders is to create the world as it could be.&quot;</em></strong> - <a href="https://farmerandfarmer.org/mastery/builder.html" target="_blank">Sep Kamvar</a></p></blockquote><p><strong>alis.exchange</strong> enables individuals and organisations to transform ideas into scalable, enterprise-grade digital services.</p><p>High-quality digital services are required to be scalable, readily accessible, secure, easy-to-use and interoperable with other digital services. The costs and expertise associated with these requirements are exacting, particularly at scale, which presents high barriers to entries for individuals and organisations.</p><p>The technologies and standards already exist to lower these barriers to entry but are disjointed and require a lot of effort to consolidate and effectively leverage before the value is realised. We have done the work of thinking through these complex problems, identifying the best-in-class open-source technologies and standards, and brought these together as alis.exchange.</p><p>Our aim is to empower anyone to create value for their organisation by democratising cloud software engineering, simplifying building, consuming and selling interoperable digital services.</p><h2 id="want-to-see-more" tabindex="-1">Want to see more? <a class="header-anchor" href="#want-to-see-more" aria-hidden="true">#</a></h2><p><strong>Consume digital services</strong><br> We aim to ensure that how software across <strong>alis.exchange</strong> operates, communicates and integrates is simple and consistent. <a href="./../consume/quick-start.html">Find out more and experience it yourself</a>.</p><p><strong>Build digital services</strong><br><strong>alis.exchange</strong> gives you and your organisation software development superpowers. <a href="./../build/overview.html">Experience what we make possible</a>.</p><p><strong>Configure your device</strong><br> Already joined <strong>alis.exchange</strong>? Follow the guides to setup your local environment, starting off with our <a href="./../configuration/command-line-interface.html">command line interface</a>.</p><h4 id="get-in-touch" tabindex="-1">Get in touch <a class="header-anchor" href="#get-in-touch" aria-hidden="true">#</a></h4><p>Have any further questions? Or want to schedule a demo? <a href="https://alis.exchange/signup" target="_blank">Get in touch</a>.</p>',12),s=[i];function n(d,l,c,h,g,u){return a(),t("div",null,s)}const f=e(o,[["render",n]]);export{m as __pageData,f as default};
