import{_ as e,o as t,c as i,a}from"./app.ba36920a.js";const p=JSON.parse('{"title":"Introduction","description":"","frontmatter":{"title":"Introduction"},"headers":[{"level":2,"title":"Want to see more?","slug":"want-to-see-more","link":"#want-to-see-more","children":[{"level":3,"title":"Consume digital services","slug":"consume-digital-services","link":"#consume-digital-services","children":[]},{"level":3,"title":"Build digital services","slug":"build-digital-services","link":"#build-digital-services","children":[]},{"level":3,"title":"Configure your device","slug":"configure-your-device","link":"#configure-your-device","children":[]}]}],"relativePath":"guides/getting-started/introduction.md"}'),r={name:"guides/getting-started/introduction.md"},o=a('<h1 id="welcome-to-the-alis-build-platform" tabindex="-1">Welcome to the Alis Build platform <a class="header-anchor" href="#welcome-to-the-alis-build-platform" aria-hidden="true">#</a></h1><blockquote><p><strong><em>&quot;Our job as builders is to create the world as it could be.&quot;</em></strong> - <a href="https://farmerandfarmer.org/mastery/builder.html" target="_blank">Sep Kamvar</a></p></blockquote><p>The Alis Build platform enables individuals and organisations to transform ideas into scalable, enterprise-grade digital services.</p><p>High-quality software needs to be scalable, readily accessible, secure, easy-to-use and interoperable with other digital services. The costs and expertise associated with these requirements are exacting, particularly at scale, which present high barriers to entry for individuals and organisations.</p><p>The technologies and standards already exist to lower these barriers, but they are disjointed and require a lot of effort to consolidate and effectively leverage before the value is realised. We have done the work of thinking through these complex problems, identifying the best-in-class open-source technologies and standards, and brought these together for the users of the platform.</p><p>Our aim is to empower anyone to create value for their organisation by democratising cloud software engineering, simplifying building, consuming and selling interoperable digital services.</p><h2 id="want-to-see-more" tabindex="-1">Want to see more? <a class="header-anchor" href="#want-to-see-more" aria-hidden="true">#</a></h2><h3 id="consume-digital-services" tabindex="-1">Consume digital services <a class="header-anchor" href="#consume-digital-services" aria-hidden="true">#</a></h3><p>We aim to ensure that how software across the platform operates, communicates and integrates is simple and consistent. <a href="./consumer-experience.html">Find out more and experience it yourself</a>.</p><h3 id="build-digital-services" tabindex="-1">Build digital services <a class="header-anchor" href="#build-digital-services" aria-hidden="true">#</a></h3><p>The Alis Build platform gives you and your organisation software development superpowers. <a href="./developer-flow.html">Experience what we make possible</a>.</p><h3 id="configure-your-device" tabindex="-1">Configure your device <a class="header-anchor" href="#configure-your-device" aria-hidden="true">#</a></h3><p>Already joined Alis Build? Follow the guides to set up your local environment, starting off with our <a href="./command-line-interface.html">command line interface</a>.</p><h4 id="get-in-touch" tabindex="-1">Get in touch <a class="header-anchor" href="#get-in-touch" aria-hidden="true">#</a></h4><p>Have any further questions? Or want to schedule a demo? <a href="https://alis.exchange/signup" target="_blank">Get in touch</a>.</p>',15),s=[o];function n(l,d,c,h,u,g){return t(),i("div",null,s)}const f=e(r,[["render",n]]);export{p as __pageData,f as default};
